mapper.select:
select * from guest
where
name=#{name} and phone=#{phone}

select * from guest
where
name=#{name} and email=#{email}

mapper.update:
update guest
set name = #{name}, password = #{password}, phone = #{phone}
where email = #{email}

update guest
set phone = #{phone}
where email = #{email}

update season_t
set money = #{money}
where st_no = #{st_no}

update guest
set mypoint = #{mypoint}
where gno = #{gno}

update coupon
set use = #{use}
where coupon_no = #{coupon_no}

update guest
set coupon_code=#{coupon_code}
where gno=#{gno}
